    public function getSourceContext()
    {
        return new Twig_Source("{# Template f√ºr die Flash Messages, diese werden im Controller aufgerufen #}

{% block flash_messages %}
    {% for type, messages in app.session.flashbag.all() %}
        {% for message in messages %}
            <div class=\"alert alert-{{ type }} alert-dismissible\" role=\"alert\">
                <button type=\"button\" class=\"close\" data-dismiss=\"alert\"><span aria-hidden=\"true\">&times;</span><span class=\"sr-only\">Close</span></button>
                {{ message | raw }}
            </div>
        {% endfor %}
    {% endfor %}
{% endblock %}", "flash_messages.html.twig", "/home/vagrant/projekte/tutorials/symfony4/basics/templates/flash_messages.html.twig");
